import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function User(props) {

    const navigate = useNavigate();

    function deleteUser(){
        (async () =>{
            try{
                await axios.delete('https://backend-iss-kin-utilisateurs.onrender.com/user/' + props.userid);
                window.location.reload();
            }catch{
                console.log("Erreur lors de la connection au server")
                //document.getElementById("container")
                //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
            }    
        })();
    }

    function goToUpdate(){
        navigate('update-user/'+ props.userid);
    }

    return ( 
        <div className='user-container'>
            <div className='user-info'>
            <img id ="user-profile" className="user-profile" src={props.userprofile} alt="profile"/>
            <p>{props.username}</p>
            </div>
           <div className="user-controls">
            <img src="icons/edit.png" width="28px" alt="edit" onClick={goToUpdate}/>
            <img src="icons/delete.png" width="28px" alt="delete" onClick={deleteUser}/>
           </div>
        </div>
        
     );
}

export default User;
