import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function User(props) {

    const navigate = useNavigate()
    
    function goToModifier(){
        navigate("update-user/" + props.userid);
    }

    function deleteUser(){
        (async () =>{
            await axios.delete('http://localhost:8800/user/' + props.userid);
        })();
    }

    return ( 
        <div className='user-container' onClick={goToModifier()}>
            <div className='user-info'>
            <img id ="user-profile" className="user-profile" src={props.userprofile} alt="profile"/>
            <p>{props.username}</p>
            </div>
           <div className="user-controls">
            <img src="icons/edit.png" width="25px" alt="edit"/>
            <img src="icons/delete.png" width="25px" alt="delete" onClick={deleteUser}/>
           </div>
        </div>
        
     );
}

export default User;
