import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function User(props) {

    const navigate = useNavigate();

    function goToRead(){
        navigate('read-user/'+ props.userid);
    }


    function capitalise(word){
        return word.charAt(0).toUpperCase()+word.slice(1)
    }

    const detectWhatsapp = (phone, text) => {
        const uri = `whatsapp://send/?phone=${encodeURIComponent(
          phone
        )}&text=${encodeURIComponent(text)}`;
        const onIE = () => {
          return new Promise((resolve) => {
            window.navigator.msLaunchUri(
              uri,
              () => resolve(true),
              () => resolve(false)
            );
          });
        };
        const notOnIE = () => {
          return new Promise((resolve) => {
            const a =
              document.getElementById("wapp-launcher") || document.createElement("a");
            a.id = "wapp-launcher";
            a.href = uri;
            a.style.display = "none";
            document.body.appendChild(a);
            const start = Date.now();
            const timeoutToken = setTimeout(() => {
              if (Date.now() - start > 1250) {
                resolve(true);
              } else {
                resolve(false);
              }
            }, 1000);
            const handleBlur = () => {
              clearTimeout(timeoutToken);
              resolve(true);
            };
            window.addEventListener("blur", handleBlur);
            a.click();
          });
        };
        return window.navigator.msLaunchUri ? onIE() : notOnIE();
      };
      
      function testWhatsapp(){
        detectWhatsapp('243893196755', 'test').then(hasWhatsapp =>
            alert(
               'You ' + 
                  (hasWhatsapp ? 'have WhatsApp' : "don't have WhatsApp")
            )
          )
      }

    //<img src="icons/edit.png" width="28px" alt="edit" onClick={goToUpdate}/>
    //<img src="icons/delete.png" width="28px" alt="delete" onClick={props.handleClick}/>

    return ( 
        <div className='user-container' onClick={goToRead}>
            <img id ="user-profile" className="user-profile" src={props.userprofile} alt="profile"/>
            <div className='user-info'>
                <h1 className='user-info-name'>{capitalise(props.usernom)} {capitalise(props.userprenom)}</h1>
                <p onClick={testWhatsapp} className="user-info-phone">+243 {props.userphone.slice(0,3)} {props.userphone.slice(3,6)} {props.userphone.slice(6,9)}</p>
            </div>
        </div>
        
     );
}

export default User;
