import React from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';

function PageUserAdd(){
    const [user, setUser] = React.useState({username:'', userprofile:''});

    React.useEffect(function(){
        setUser(prev=>({...prev, userprofile:defaultImage}))
    },[])


    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function addUser(){
        await axios.post('http://localhost:8800/user', user)
    }

    function submit(){
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        addUser();
        goToApp();
    }

      function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            setUser(user=>({...user, userprofile:reader.result}));
            console.log(user)
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
        <div className="app-container">
            <h1>Nouveau utilisateur</h1>
            <br />
            <input id ="img-file" type="file" onChange={previewFile} />
            <img id ="user-profile" style={{width:'200px', Height:'200px', margin:'20px', border:'3px white solid', cursor:'pointer', borderRadius:'48%'}} src={user.userprofile} alt="profile"/>
           <input style={{fontSize:'1rem', fontWeight:'bold'}} type="text" name="username" onChange={handleChange} placeholder='Entrez votre nom' value={user.username}/>
           <br />
            <div className='inputs-controls'>
              <button className='btn-cancel' onClick={goToApp}>ANULLER</button>
            <button className='btn-add' onClick={submit}>AJOUTER</button>
            </div>
        </div>
    )
}
export default PageUserAdd;
