import React from 'react';
import {useLocation, useNavigate} from 'react-router-dom';
import axios from 'axios';

function PageUserEdit(){
    const location = useLocation();

    const [user, setUser] = React.useState({nameuser:''});
    const locationiduser= location.pathname.split('/')[2];

    React.useEffect(function(){
        async function getUserData(iduser){
            const res = await axios.get('http://localhost:8800/user/' + iduser )
            setUser(prev=>({...prev, nameuser:res.data[0].nameuser}))
        }
        getUserData(locationiduser);
    },[locationiduser,user])
    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function editUser(){
        await axios.put('http://localhost:8800/user/' + user.iduser, user)
    }

    function submit(){
        if(user.nameuser === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        editUser()
        alert('Utilisateur Modifié')
        goToApp();
    }

    return(
    <div>
            <h1>Modifier utilisateur</h1>
            <br/>
           <input type="numer" name="nameuser" value ={user.nameuser} onChange={handleChange}/>
           <br/>
           <button onClick={goToApp}>ANULLER</button>
           <button onClick={submit}>MODIFIER</button>
        </div>
    )
}
export default PageUserEdit;
