import React from 'react';
import axios from 'axios';
import User from '../components/user';
import { useNavigate } from 'react-router-dom';

function PageApp(){
    const [users, setUsers] = React.useState([]);

    React.useEffect(function(){
        async function fetchAllUsers(){
            try{
                const res = await axios.get('http://localhost:8800/users');
                setUsers(res.data);
            }catch{
                document.getElementById("container")
                .innerHTML = "<p>Erreur lors de la connection au server.</p>";
            }          
        }
        fetchAllUsers();
    },[users])

    let usersElements = users.map(
        user=>{
            return(
              <User
                key = {user.userid}
                {...user}
              />
                )      
        }      
    );

    const navigate = useNavigate()

    function goToAdd(){
        navigate("/new-user");
    }

    return(
       <div className="app-container" id="container">
            <h1>Siteweb des Utilisateurs</h1>
            <button onClick={goToAdd}>CREER UTILISATEUR</button>
            <h1>Voici la liste des utilisateurs :</h1>
            <div className='user-list-container'>
            {usersElements}
            </div>     
        </div>
    )
}
export default PageApp;
