import React from 'react';
import {useNavigate} from 'react-router-dom';
import {useLocation} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';
import HeaderUser from '../components/headerUser';

function PageUserRead(){
    const [user, setUser] = React.useState({username:'', userprofile:defaultImage});
    const [pageLoaded, setLoaded] = React.useState(false);
    const location = useLocation();

    const locationuserid= location.pathname.split('/')[2];
    console.log('read user :' + locationuserid)

    React.useEffect(function(){
        async function getUserData(iduser){
            try{
              const appMessage = document.getElementById('app-message');
              console.log('send request')
              appMessage.innerHTML = waitElement;
              const res = await axios.get('https://backend-iss-kin-utilisateurs.onrender.com/user/' + iduser )
              appMessage.style.display="none";
              console.log('get response')
              setUser({username:res.data[0].username, userprofile:res.data[0].userprofile})
              setLoaded(true)
            }catch(e){
              const appMessage = document.getElementById('app-message');
                appMessage.innerHTML = requestFalureElement;
            console.log("Erreur lors de la connection au server" + e)
        }
            
        }
        getUserData(locationuserid);
    },[locationuserid])

    const navigate = useNavigate() 

    function goToApp(){
      navigate("/")
    }

    const waitElement = `<img
                            src="icons/wait.gif"
                            style="width:80%;"
                          />`
    const requestFalureElement = `<img
                                        src="icons/no-wifi.png"
                                        style="width:100%;"
                                    />
                                    <p 
                                    style="font-weight:bolder;
                                    font-size:1rem;
                                     margin:20px;
                                     text-align:center;
                                      color: orange">
                                    Erreur au niveau du serveur
                                    </p>
                                    <small style="font-weight:small;font-size:small; margin:20px; text-align:center">
                                        Veuillez r√©essayer plus tard
                                    </small>`

    return(
         <div className='form'>
              {!pageLoaded && <div id = "app-message" className="app-message"></div>}
               {pageLoaded && (
                <div className="form">
                  <HeaderUser type="user-add"/>
                  <p id="info-message" className="info-message"></p>
                  <div className="user-profile-input">
                    <img id ="user-profile" className="user-profile" src={user.userprofile} alt="profile"/>
                  </div>
                  <br />
                  <h1>{user.username}</h1>
                  </div>
               )}
          </div>
    )
}
export default PageUserRead;
