import React from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';

function PageUserAdd(){
    const [user, setUser] = React.useState({nameuser:''});
    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    function addUser(){
        if(user.nameuser === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        alert("Utilisateur ajouté")
        goToApp();
    }

    React.useEffect(function(){
        async function fetchAllUsers(){
            const res = await axios.get('http://localhost:8800/users');
            setUsers(res.data);
        }
        fetchAllUsers();
    },[])

    return(
        <div>
            <h1>Nouveau utilisateur</h1>
            <br/>
           <input type="numer" name="nameuser" onChange={handleChange}/>
           <br/>
           <button onClick={goToApp}>ANULLER</button>
           <button onClick={addUser}>AJOUTER</button>
        </div>
    )
}
export default PageUserAdd;
