import React from 'react';
import {useLocation, useNavigate} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';
import HeaderUser from '../components/headerUser';

function PageUserEdit(){
    const location = useLocation();

    const [user, setUser] = React.useState({username:'', userprofile:defaultImage});

    const locationuserid= location.pathname.split('/')[2];
    console.log(locationuserid)

    React.useEffect(function(){
        async function getUserData(iduser){
            try{
              const appContainer = document.getElementById('app-container')
              appContainer.style.display = 'none'
              const res = await axios.get('http://localhost:8800/user/' + iduser )
              appContainer.style.display = 'block'
              setUser({username:res.data[0].username, userprofile:res.data[0].userprofile})
            }catch(e){
            console.log("Erreur lors de la connection au server" + e)
            //document.getElementById("container")
            //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
        }
            
        }
        getUserData(locationuserid);
    },[locationuserid])
    
    function handleChange(event){
        setUser(values=> ({...values, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function editUser(){
        try{
          await axios.put('http://localhost:8800/user/' + locationuserid, user)
          window.location.reload();
        }catch{
          console.log("Erreur lors de la connection au server")
        //document.getElementById("container")
        //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
    }
    }

    function submit(){
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        console.log(user)
        editUser()
        goToApp();   
    }

    function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            setUser(user=>({...user, userprofile:reader.result}));
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
      <div>
      <HeaderUser type="NOUVEAU UTILISATEUR"/>
      <p className="info-message" id="info-message">Veuillez patienter ...</p>
      <div className='form'>
          <div className="user-profile-input">
            <img id ="user-profile" className="user-profile" src={user.userprofile} alt="profile" onClick={importData}/>
            <img src="icons/camera.png" alt="camera" className="btn-camera" onClick={importData}/>
          </div>
          <input autoComplete='off' maxLength={15}  type="text" name="usernom" onChange={handleChange} placeholder='Votre nom' value={user.usernom}/>
          <input autoComplete='off' maxLength={15}  type="text" name="userprenom" onChange={handleChange} placeholder='Votre prenom' value={user.userprenom}/>
          <div className="phone-input">
            <label htmlFor="userphone">+243</label>
            <input autoComplete='off' type="number" name="userphone" placeholder='Votre telephone' onChange={handleChange} onKeyPress={handlePhone} value={user.userphone}/>
          </div>
          <button className='btn-add-user' onClick={submit} id="btn-ajout">AJOUTER</button>
      </div>
      </div>  
    )
}
export default PageUserEdit;
