import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function User(props) {

    const navigate = useNavigate()
    
    function goToModifier(){
        navigate("update-user/" + props.userid);
    }

    function deleteUser(){
        (async () =>{
            await axios.delete('http://localhost:8800/user/' + props.userid);
        })();
        window.location.reload();
    }

    return ( 
        <div style={{display:'flex', alignItems:'center', gap:'15px', margin:'15px'}}>
            <img id ="user-profile" style={{width:'100px', Height:'80px', margin:'10px', border:'3px white solid',borderRadius:'48%'}} src={props.userprofile} alt="profile"/>
            <p style={{fontSize:'1.5rem', fontWeight:'bold'}}>{props.username}</p>
            <button onClick={goToModifier}>Modifier</button> 
            <button onClick={deleteUser}>Supprimer</button>
        </div>
        
     );
}

export default User;
