import React from 'react';
import {useLocation, useNavigate} from 'react-router-dom';
import axios from 'axios';

function PageUserEdit(){
    const location = useLocation();

    const [user, setUser] = React.useState({username:'', userprofile:''});
    const locationuserid= location.pathname.split('/')[2];

    React.useEffect(function(){
        async function getUserData(iduser){
            const res = await axios.get('http://localhost:8800/user/' + iduser )
            document.getElementById('user-profile').src = res.data[0].userprofile;
            document.getElementById('username').value = res.data[0].username;
            setUser(setUser(values=>({username:res.data[0].userprofile, userprofile:res.data[0].username})))
        }
        getUserData(locationuserid);
    },[locationuserid])
    
    function handleChange(event){
        setUser(values=> ({...values, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function editUser(){
        await axios.put('http://localhost:8800/user/' + locationuserid, user)
    }

    function submit(){
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        editUser()
        goToApp();
        window.location.reload();
    }

    function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            document.getElementById('user-profile').src = reader.result;
            setUser(user=>({...user, userprofile:reader.result}));
            console.log(user)
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
        <div className="app-container">
        <h1>Nouveau utilisateur</h1>
        <br />
        <input id ="img-file" type="file" onChange={previewFile} />
        <img id ="user-profile" style={{width:'200px', Height:'200px', margin:'20px', border:'3px white solid', cursor:'pointer', borderRadius:'48%'}} src="img/user-profile.png" alt="profile"/>
       <input id = "username" style={{fontSize:'1rem', fontWeight:'bold'}} type="text" name="username" onChange={handleChange} placeholder='Entrez votre nom'/>
       <br />
        <div className='inputs-controls'>
          <button className='btn-cancel' onClick={goToApp}>ANULLER</button>
        <button className='btn-add' onClick={submit}>MODIFIER</button>
        </div>
    </div>
    )
}
export default PageUserEdit;
