import React from 'react';
import axios from 'axios';
import User from '../components/user';
import Header from '../components/header';
import Footer from '../components/footer';

function PageApp(){
    const [users, setUsers] = React.useState([]);

    React.useEffect(function(){
        async function fetchAllUsers(){
            try{
                const appMessage = document.getElementById('app-message');
                appMessage.innerHTML = waitElement;
                const res = await axios.get('https://bckend-iss-kin-utilisateurs.onrender.com/users');
                appMessage.innerHTML = '';
                console.log('get response')
                setUsers(res.data);
            }catch{
                const appMessage = document.getElementById('app-message');
                appMessage.innerHTML = requestFalureElement;
                //document.getElementById("container")
                //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
            }          
        }
        fetchAllUsers();
    },[])

    let usersElements = users.map(
        user=>{
            return(
              <User
                key = {user.userid}
                {...user}
              />
                )      
        }      
    );
    
    /*function deleteUser(props){
        (async () =>{
            const appMessage = document.getElementById('app-message')
            const appFooter = document.getElementById('footer')
            try{
                appMessage.style.display = "block";
                appFooter.style.display="none"
                await axios.delete('https://backend-iss-kin-utilisateurs.onrender.com/user/' + props.userid);
                appMessage.style.display = "none";
                appFooter.style.display="flex"
            }catch{
                console.log("Erreur lors de la connection au server")
            }    
        })();
    }*/

    const waitElement = `<img
                            src="icons/wait.gif"
                            style="width:100%;"
                          />`
    const requestFalureElement = `<p style="font-weight:bold;font-size:large; margin:20px; text-align:center">
                                    Il y a une erreur au niveau du serveur
                                    </p>
                                    <small style="font-weight:small;font-size:large; margin:20px; text-align:center">
                                        Veuillez me contacer pour me signaler ce problème
                                    </small>`

    return(
       <div className="app-container">
            <Header/>
            <div id = "app-message" className="app-message"></div>
            <div className='user-list-container'>
            {usersElements}
            </div>
            <Footer/>     
        </div>
    )
}
export default PageApp;
