import React from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';

function PageUserAdd(){
    const [user, setUser] = React.useState({nameuser:''});
    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function addUser(){
        await axios.post('http://localhost:8800/user', user)
    }

    function submit(){
        if(user.nameuser === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        addUser()
        alert('Utilisateur Ajouté')
        goToApp();
    }

    return(
        <div>
            <h1>Nouveau utilisateur</h1>
            <br/>
           <input type="numer" name="nameuser" onChange={handleChange}/>
           <br/>
           <button onClick={goToApp}>ANULLER</button>
           <button onClick={submit}>AJOUTER</button>
        </div>
    )
}
export default PageUserAdd;
