import React from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import getDefaultImage from '../components/defaultImage';

function PageUserAdd(){
    const [user, setUser] = React.useState({username:'', userprofile:''});

    React.useEffect(function(){
        setUser(prev=>({...prev, userprofile:getDefaultImage}))
    },[])


    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function addUser(){
        await axios.post('http://localhost:8800/user', user)
    }

    function submit(){
        console.log(user)
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }

    }

    function encodeImageFileAsURL(image) {
        let file = image.files[0];
        let reader = new FileReader();
        reader.onloadend = function() {
            return reader.result
        }
        reader.readAsDataURL(file);
      }

      function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            document.getElementById('user-profile').src = reader.result;
            setUser(values=>({...user, userprofile:reader.result}));
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
        <div>
            <center>
            <h1>Nouveau utilisateur</h1>
            <br/>
            <input id ="img-file" type="file" onChange={previewFile} />
            <br />
            <img id ="user-profile" style={{width:'300px', Height:'300px', margin:'20px', border:'3px white solid', cursor:'pointer', borderRadius:'48%'}} src="img/default-profile.jpg" alt="profile"/>
           <input type="text" name="username" onChange={handleChange} placeholder='Entrez votre nom'/>
           <br/>
           <br />
           <button onClick={goToApp}>ANULLER</button>
           <button onClick={submit}>AJOUTER</button>
            </center>
        </div>
    )
}
export default PageUserAdd;
