import React from 'react';
import {useNavigate} from 'react-router-dom';
import {useLocation} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';
import HeaderUser from '../components/headerUser';

function PageUserEdit(){
    const [user, setUser] = React.useState({usernom:'', userprenom:'', userphone:'', userprofile:defaultImage});
    const [pageLoaded, setLoaded] = React.useState(false);
    const location = useLocation();

    const locationuserid= location.pathname.split('/')[2];
    console.log('read user :' + locationuserid)

    React.useEffect(function(){
        async function getUserData(iduser){
            try{
              const appMessage = document.getElementById('app-message');
              console.log('send request')
              appMessage.innerHTML = waitElement;
              const res = await axios.get('http://localhost:8800/user/' + iduser )
              appMessage.style.display="none";
              console.log('get response')
              setUser({usernom:res.data[0].usernom,userprenom:res.data[0].userprenom,userphone:res.data[0].userphone, userprofile:res.data[0].userprofile})
              setLoaded(true)
            }catch(e){
              const appMessage = document.getElementById('app-message');
                appMessage.innerHTML = requestFalureElement;
            console.log("Erreur lors de la connection au server" + e)
        }
            
        }
        getUserData(locationuserid);
    },[locationuserid])

    const navigate = useNavigate() 

    function goToEdit(){
      navigate("/update-user/"+user.locationuserid)
    }

    function capitalise(word){
      return word.charAt(0).toUpperCase()+word.slice(1)
  }

    const waitElement = `<img
                            src="icons/wait.gif"
                            style="width:80%;"
                          />`
    const requestFalureElement = `<img
                                        src="icons/no-wifi.png"
                                        style="width:100%;"
                                    />
                                    <p 
                                    style="font-weight:bolder;
                                    font-size:1rem;
                                     margin:20px;
                                     text-align:center;
                                      color: orange">
                                    Erreur au niveau du serveur
                                    </p>
                                    <small style="font-weight:small;font-size:small; margin:20px; text-align:center">
                                        Veuillez r√©essayer plus tard
                                    </small>`



    return(
      <div className='app-container'>
        <HeaderUser type="INFO DE L'UTILISATEUR"/>
              {!pageLoaded && <div id = "app-message" className="app-message"></div>}
               {pageLoaded && (
                <div className="form">
                        <HeaderUser type="NOUVEAU UTILISATEUR"/>
                <p className="info-message" id="info-message">Veuillez patienter ...</p>
                <div className='form'>
                    <div className="user-profile-input">
                      <img id ="user-profile" className="user-profile" src={user.userprofile} alt="profile" onClick={importData}/>
                      <img src="icons/camera.png" alt="camera" className="btn-camera" onClick={importData}/>
                    </div>
                    <input autoComplete='off' maxLength={15}  type="text" name="usernom" onChange={handleChange} placeholder='Votre nom' value={user.usernom}/>
                    <input autoComplete='off' maxLength={15}  type="text" name="userprenom" onChange={handleChange} placeholder='Votre prenom' value={user.userprenom}/>
                    <div className="phone-input">
                      <label htmlFor="userphone">+243</label>
                      <input autoComplete='off' type="number" name="userphone" placeholder='Votre telephone' onChange={handleChange} onKeyPress={handlePhone} value={user.userphone}/>
                    </div>
                    <button className='btn-add-user' onClick={submit} id="btn-ajout">AJOUTER</button>
          </div>
                </div>
               )}
        {pageLoaded && (
          <div className="user-controls">
          <img src="icons/delete.png" alt="delete" width={45}/>
          <img src="icons/edit.png" alt="edit" width={45} onClick={goToEdit}/>
      </div>
        )}
      </div>
    )
}
export default PageUserEdit;
