import React from 'react';
import {useLocation, useNavigate} from 'react-router-dom';
import axios from 'axios';

function PageUserEdit(){
    const location = useLocation();

    const [user, setUser] = React.useState({iduser:null, nameuser:''});
    const locationiduser= location.pathname.split('/')[2];

    React.useEffect(function(){
        async function getUserData(iduser){
            const res = await axios.get('http://localhost:8800/user/' + iduser )
            setUser(prev=>({...user, nameuser:res.data[0].nameuser}))
        }
        getUserData(locationiduser);
    },[])
    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function addUser(){
        await axios.post('http://localhost:8800/user', user)
    }

    function submit(){
        if(user.nameuser === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        addUser()
        alert('Utilisateur Ajouté')
        goToApp();
    }

    return(
        <div>
            <h1>Nouveau utilisateur</h1>
            <br/>
           <input type="numer" name="nameuser" onChange={handleChange}/>
           <br/>
           <button onClick={goToApp}>ANULLER</button>
           <button onClick={submit}>AJOUTER</button>
        </div>
    )
}
export default PageUserEdit;
