import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function User(props) {

    const navigate = useNavigate()
    
    function goToModifier(){
        navigate("update-user/" + props.userid);
    }

    function deleteUser(){
        (async () =>{
            await axios.delete('http://localhost:8800/user/' + props.userid);
        })();
    }

    return ( 
        <div style={{width:'90%',display:'flex', alignItems:'center', justifyContent:'space-around', margin:'15px', border:'solid 2px white', padding:'5'}}>
            <div>
            <img id ="user-profile" style={{width:'120px', Height:'120px', border:'3px white solid',borderRadius:'48%'}} src={props.userprofile} alt="profile"/>
            <p style={{fontSize:'1.5rem', fontWeight:'bold'}}>{props.username}</p>
            </div>
           <div>
           <button onClick={goToModifier}>Modifier</button> 
            <button onClick={deleteUser}>Supprimer</button>
           </div>
        </div>
        
     );
}

export default User;
