import React from 'react';
import {useLocation, useNavigate} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';

function PageUserEdit(){
    const location = useLocation();

    const [user, setUser] = React.useState({username:'', userprofile:defaultImage});

    const locationuserid= location.pathname.split('/')[2];

    React.useEffect(function(){
        async function getUserData(iduser){
          const appMessage = document.getElementById('app-message')
            try{
              appMessage.textContent = "Veuillez patienter ..."
              const res = await axios.get('http://localhost:8800/user/' + iduser )
              appMessage.textContent = ""
              setUser({username:res.data[0].username, userprofile:res.data[0].userprofile})
            }catch{
            alert("Erreur lors de la connection au server")
            //document.getElementById("container")
            //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
        }
            
        }
        getUserData(locationuserid);
    },[locationuserid])
    
    function handleChange(event){
        setUser(values=> ({...values, [event.target.name] : event.target.value}))
        console.log(user);
    }

    const navigate = useNavigate() 

    function goToApp(){
        navigate("/");
    }

    async function editUser(){
        try{
          await axios.put('http://localhost:8800/user/' + locationuserid, user)
          window.location.reload();
        }catch{
          console.log("Erreur lors de la connection au server")
        //document.getElementById("container")
        //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
    }
    }

    function submit(){
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        console.log(user)
        editUser()
        goToApp();   
    }

    function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            setUser(user=>({...user, userprofile:reader.result}));
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
        <div className="app-container">
          <p className="app-message"></p>
          <h1>Modifier utilisateur</h1>
          <br />
          <input id ="img-file" type="file" onChange={previewFile} />
          <img id ="user-profile" style={{width:'200px', Height:'200px', margin:'20px', border:'3px white solid', cursor:'pointer', borderRadius:'48%'}} src={user.userprofile} alt="profile"/>
          <input id = "username" maxLength={20}style={{fontSize:'1rem', fontWeight:'bold'}} type="text" name="username" onChange={handleChange} placeholder='Entrez votre nom et prenom' value={user.username}/>
          <br />
          <div className='inputs-controls'>
            <button className='btn-cancel' onClick={goToApp}>ANULLER</button>
          <button className='btn-add' onClick={submit}>MODIFIER</button>
        </div>
    </div>
    )
}
export default PageUserEdit;
