                await axios.delete('https://backend-iss-kin-utilisateurs.onrender.com/user/' + props.userid);
                appMessage.style.display = "none";
                appFooter.style.display="flex"
            }catch{
                console.log("Erreur lors de la connection au server")
            }    
        })();
    }*/

    const waitElement = `<img
                            src="icons/wait.gif"
                            style="width:80%;"
                          />`
    const requestFalureElement = `<img
                                        src="icons/no-wifi.png"
                                        style="width:100%;"
                                    />
                                    <p 
                                    style="font-weight:bolder;
                                    font-size:1rem;
                                     margin:20px;
                                     text-align:center;
                                      color: orange">
                                    Erreur au niveau du serveur
                                    </p>
                                    <small style="font-weight:small;font-size:small; margin:20px; text-align:center">
                                        Veuillez réessayer plus tard
                                    </small>`
                console.log(pageLoaded)
                console.log('render')
                console.log(users)

    return(
       <div className="app-container">
            <Header/>
            {pageLoaded && usersElements.length === 0 && (
                <p>Aucun Utilisateur</p>
            )}
            {pageLoaded &&(
             <div className='user-list-container'>
                {usersElements}
            </div>)}
            <div id = "app-message" className="app-message"></div>
            {pageLoaded && <Footer/>}    
        </div>
    )
}
export default PageApp;
