import React from 'react';
import {useNavigate} from 'react-router-dom';
import axios from 'axios';
import defaultImage from '../components/defaultImage';
import HeaderUser from '../components/headerUser';

function PageUserAdd(){
    const [user, setUser] = React.useState({username:'', userprofile:defaultImage});
    
    function handleChange(event){
        setUser(prev=> ({...prev, [event.target.name] : event.target.value}))
    }

    const navigate = useNavigate() 

    function goToApp(){
      document.getElementById('info-message').textContent = 'Veuillez patienter ...'
      setTimeout(() => {
        navigate("/")
      }, 2000 );
    }

    async function addUser(){
      try{
        console.log('send request')
        await axios.post('https://backend-iss-kin-utilisateurs.onrender.com/user', user)
      }catch{
        console.log("Erreur lors de l'ajout")
          //document.getElementById("container")
          //.innerHTML = "<p>Erreur lors de la connection au server.</p>";
      }
    }

    function submit(){
        if(user.username === ''){
            alert('Vous devez remplir le champ')
            return;
        }
        addUser();
        goToApp();
    }

      function convertToBase64(file){
        const reader = new FileReader();
        reader.addEventListener(
          "load",
          () => {
            setUser(user=>({...user, userprofile:reader.result}));
            console.log(user)
          },
          false,
        );
      
        if (file) {
          reader.readAsDataURL(file);
        }       
      }

      function previewFile() {
        const file = document.getElementById("img-file").files[0];
        convertToBase64(file);
      }

    return(
         <div className='form'>
              <HeaderUser type="user-add"/>
              <p id="info-message" class="info-message"></p>
              <input id ="img-file" type="file" onChange={previewFile}/>
              <img id ="user-profile" className="user-profile-entry" style={{width:'200px', Height:'200px', margin:'20px', border:'3px white solid', cursor:'pointer', borderRadius:'48%'}} src={user.userprofile} alt="profile"/>
              <input autoComplete='off' maxLength={20}  type="text" name="username" onChange={handleChange} placeholder='Entrez votre nom et prenom' value={user.username}/>
              <br />
              <button className='btn-add-user' onClick={submit}>AJOUTER</button>
          </div>
    )
}
export default PageUserAdd;
