import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function User(props) {

    const navigate = useNavigate();

    function deleteUser(){
        (async () =>{
            await axios.delete('http://localhost:8800/user/' + props.userid);
        })();
    }

    function goToUpdate(){
        navigate('/update-user/'+ props.userid);
    }

    return ( 
        <div className='user-container'>
            <div className='user-info'>
            <img id ="user-profile" className="user-profile" src={props.userprofile} alt="profile"/>
            <p>{props.username}</p>
            </div>
           <div className="user-controls">
            <img src="icons/edit.png" width="25px" alt="edit" onClick={goToUpdate}/>
            <img src="icons/delete.png" width="25px" alt="delete" onClick={deleteUser}/>
           </div>
        </div>
        
     );
}

export default User;
